    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        FornecedorBLL fBLL = new FornecedorBLL();
        
        fBLL.setRazaoSocial(txtRazaoSocial.getText());
        fBLL.setCnpj(txtCNPJ.getText().replace(".", "").replace("-", "").replace("/", ""));
        fBLL.getEndereco().setCep(txtCep.getText().replace(".", "").replace("-", ""));
        fBLL.getEndereco().setLogradouro(cmbLogradouro.getSelectedItem()+" "+txtLogradouro.getText());
        fBLL.getEndereco().getBairro().setNome(txtBairro.getText());
        fBLL.getEndereco().setNumero(txtNumero.getText());
        fBLL.getEndereco().getBairro().getCidade().setNome(txtCidade.getText());
        fBLL.getEndereco().getBairro().getCidade().getEstado().setNome(txtEstado.getText());
        fBLL.getEndereco().getBairro().getCidade().getEstado().getPais().setPaisPt(txtPais.getText());
        
        
        TelefoneBLL telefone = new TelefoneBLL();
        telefone.setDdd(txtddd.getText().replace("(", "").replace(")", ""));
        telefone.setNumero(txtTelefone.getText().replace("-", ""));
        telefone.setAtivo(true);
        
        TelefoneBLL celular = new TelefoneBLL();
        celular.setDdd(txtddd1.getText().replace("(", "").replace(")", ""));
        celular.setNumero(txtCelular.getText().replace("-", ""));
        celular.setAtivo(true);
        
        fBLL.getTelefones().add(celular);
        fBLL.getTelefones().add(telefone);
        
        fBLL.setEmail(txtEmail.getText());
        fBLL.setAtivo(true);
        
        if(fBLL.Cadastrar()){
            JOptionPane.showMessageDialog(null, "CADASTROU O FORNECEDOR", "Cadastro efetuado com sucesso", JOptionPane.INFORMATION_MESSAGE);
        } else {
            JOptionPane.showMessageDialog(null, "NÃO DEU CERTO :(", "Erro", JOptionPane.ERROR_MESSAGE);
        }
        Limpar();

    }//GEN-LAST:event_btnCadastrarActionPerformed

    public void Limpar(){
        txtBairro.setText("");
        txtCNPJ.setText("");
        txtCelular.setText("");
        txtCep.setText("");
        txtCidade.setText("");
        txtEmail.setText("");
        txtEstado.setText("");
        txtLogradouro.setText("");
        txtNumero.setText("");
        txtPais.setText("");
        txtRazaoSocial.setText("");
        txtTelefone.setText("");
        txtddd.setText("");
        txtddd1.setText("");
    }
    
